mapper.update:
update pig_file

		pig_status = #{pigStatus,jdbcType=INTEGER},
		update_time = #{updateTime,jdbcType=TIMESTAMP}

where pig_id = #{pigId,jdbcType=VARCHAR}

mapper.select:
SELECT brand.id as id, brand.sign as sign, pf.create_time as createTime, sue.`name` as name, pf.famer_id as farmId, pf.mark as remark FROM brand LEFT JOIN pig_file AS pf ON brand.sign = pf.pig_id LEFT JOIN s_user AS sue ON sue.Id=pf.famer_id WHERE brand.sign_status=1
AND brand.creater_id=#{userId}
AND brand.sign=#{sign}
LIMIT #{start},#{length}

SELECT COUNT(brand.id ) FROM brand LEFT JOIN pig_file AS pf ON brand.sign = pf.pig_id LEFT JOIN s_user AS sue ON sue.Id=pf.famer_id WHERE brand.sign_status=1
AND brand.creater_id=#{userId}
AND brand.sign=#{sign}

SELECT pig_file.id AS id, pig_file.pig_id AS sign, pig_breed.`name` AS pigType, s_user.`name` AS bulider, pig_file.pig_status AS statusType, pig_file.create_time AS createTime FROM pig_file LEFT JOIN s_user ON s_user.Id = pig_file.create_people_id LEFT JOIN pig_breed ON pig_file.pig_type = pig_breed.id WHERE pig_file.symbol=0
AND pig_file.famer_id = #{userId}
AND(pig_file.pig_id= #{keyWords} OR s_user.`name` LIKE '${keyWords}%')
LIMIT #{start},#{length}

SELECT COUNT(pig_file.id) FROM pig_file LEFT JOIN s_user ON s_user.Id = pig_file.create_people_id LEFT JOIN pig_breed ON pig_file.pig_type = pig_breed.id WHERE pig_file.symbol=0
AND pig_file.famer_id = #{userId}
AND(pig_file.pig_id= #{keyWords} OR s_user.`name` LIKE '${keyWords}%')

SELECT COUNT(file.pig_id) FROM pig_file file
LEFT JOIN s_user u ON u.Id = file.famer_id
where 1 = 1

	and u.province_id = #{areaId}


	and u.city_id = #{areaId}


	and u.district_id = #{areaId}


	and u.town_id = #{areaId}


	and u.village_id = #{areaId}

SELECT COUNT(brand.sign) FROM brand
LEFT JOIN s_user u ON u.Id = brand.creater_id
where 1 = 1
AND brand.create_time IS NOT NULL AND brand.sign_status = 1

	and u.province_id = #{areaId}


	and u.city_id = #{areaId}


	and u.district_id = #{areaId}


	and u.town_id = #{areaId}


	and u.village_id = #{areaId}

AND DATE_FORMAT(brand.create_time,'%y-%m-%d') = DATE_FORMAT(NOW(),'%y-%m-%d')

select sum(case month(brand.create_time) when '1' then 1 else 0 end) as jan, sum(case month(brand.create_time) when '2' then 1 else 0 end) as feb, sum(case month(brand.create_time) when '3' then 1 else 0 end) as mar, sum(case month(brand.create_time) when '4' then 1 else 0 end) as apr, sum(case month(brand.create_time) when '5' then 1 else 0 end) as may, sum(case month(brand.create_time) when '6' then 1 else 0 end) as jun, sum(case month(brand.create_time) when '7' then 1 else 0 end) as jul, sum(case month(brand.create_time) when '8' then 1 else 0 end) as aug, sum(case month(brand.create_time) when '9' then 1 else 0 end) as sept, sum(case month(brand.create_time) when '10' then 1 else 0 end) as oct, sum(case month(brand.create_time) when '11' then 1 else 0 end) as nov, sum(case month(brand.create_time) when '12' then 1 else 0 end) as dece from brand LEFT JOIN s_user u ON u.Id = brand.creater_id where year(brand.create_time)= DATE_FORMAT(NOW(), '%Y') AND brand.create_time IS NOT NULL AND brand.sign_status = 1
and u.province_id = #{areaId}
and u.city_id = #{areaId}
and u.district_id = #{areaId}
and u.town_id = #{areaId}
and u.village_id = #{areaId}

select sum(case month(file.create_time) when '1' then 1 else 0 end) as jan, sum(case month(file.create_time) when '2' then 1 else 0 end) as feb, sum(case month(file.create_time) when '3' then 1 else 0 end) as mar, sum(case month(file.create_time) when '4' then 1 else 0 end) as apr, sum(case month(file.create_time) when '5' then 1 else 0 end) as may, sum(case month(file.create_time) when '6' then 1 else 0 end) as jun, sum(case month(file.create_time) when '7' then 1 else 0 end) as jul, sum(case month(file.create_time) when '8' then 1 else 0 end) as aug, sum(case month(file.create_time) when '9' then 1 else 0 end) as sept, sum(case month(file.create_time) when '10' then 1 else 0 end) as oct, sum(case month(file.create_time) when '11' then 1 else 0 end) as nov, sum(case month(file.create_time) when '12' then 1 else 0 end) as dece from pig_file file LEFT JOIN s_user u ON u.Id = file.famer_id where year(file.create_time)= DATE_FORMAT(NOW(), '%Y')
and u.province_id = #{areaId}
and u.city_id = #{areaId}
and u.district_id = #{areaId}
and u.town_id = #{areaId}
and u.village_id = #{areaId}

		SELECT
		pig_file.id AS id,
		pig_file.pig_id AS sign,
		pig_breed.`name` AS pigType,
		pig_file.pig_status AS pigStatus,
		DATE_FORMAT(pig_file.create_time,'%Y-%m-%d')  AS createTime
		FROM
	    pig_file 
		LEFT JOIN  pig_breed  ON pig_file.pig_type = pig_breed.id
		WHERE
		pig_file.symbol=0
		AND pig_file.pig_status=0 
		OR pig_file.pig_status=1
        AND pig_file.famer_id=#{id}

			AND pig_file.create_time >=
			#{beginDate}


			AND pig_file.create_time <=#{endDate}


			AND pig_file.pig_id = #{sign}


			AND pig_file.pig_type = #{pigType}


			AND pig_file.pig_status = #{pigStatus}
